import{_ as a}from"./chunks/happ.DLnbAQ_X.js";import{_ as o,c as e,o as r,ag as p}from"./chunks/framework.Brz8Y0aD.js";const s="/img/apps/android/happ/install_1.png",l="/img/apps/android/happ/install_2.png",n="/img/apps/android/happ/install_3.png",i="/img/apps/android/happ/install_4.png",f=JSON.parse('{"title":"Настройка Happ на Android","description":"Пошаговая инструкция по установке и настройке приложения Happ на смартфонах и планшетах с Android для подключения к Tesla VPN по протоколу VLESS.","frontmatter":{"id":"setup-happ-android","title":"Настройка Happ на Android","description":"Пошаговая инструкция по установке и настройке приложения Happ на смартфонах и планшетах с Android для подключения к Tesla VPN по протоколу VLESS.","keywords":["Happ","Tesla VPN","VLESS","настройка VPN на Android","VPN для Android","как настроить Happ"],"head":[["meta",{"name":"og:image"},{"content":"/img/banners/happ.png"}]]},"headers":[],"relativePath":"ConnectDevice/Android/Happ.md","filePath":"ConnectDevice/Android/Happ.md","lastUpdated":null}'),d={name:"ConnectDevice/Android/Happ.md"};function c(g,t,m,h,_,b){return r(),e("div",null,t[0]||(t[0]=[p('<p><img src="'+a+'" alt="Шапка" data-zoomable="true"></p><h1 id="настроика-happ-на-android" tabindex="-1">Настройка Happ на Android <a class="header-anchor" href="#настроика-happ-на-android" aria-label="Permalink to &quot;Настройка Happ на Android&quot;">​</a></h1><p>Эта инструкция поможет вам установить и настроить приложение <strong>Happ</strong> для подключения к <strong>Tesla VPN</strong> на вашем Android-устройстве.</p><table tabindex="0"><thead><tr><th style="text-align:center;">Требования</th><th style="text-align:center;">Ссылки для загрузки</th></tr></thead><tbody><tr><td style="text-align:center;">Android 5.0 или новее</td><td style="text-align:center;"><a href="https://play.google.com/store/apps/details?id=com.happproxy" target="_blank" rel="noreferrer"><strong>Скачать из Google Play</strong></a> | <a href="https://github.com/Happ-proxy/happ-android/releases/latest/download/Happ.apk" target="_blank" rel="noreferrer"><strong>Скачать APK</strong></a></td></tr></tbody></table><blockquote><p>Happ — это простой и удобный мультиплатформенный клиент, который позволяет безопасно использовать VPN с поддержкой протокола VLESS, используемого в <strong>Tesla VPN</strong>.</p></blockquote><div class="danger custom-block"><p class="custom-block-title">Важная информация</p><p>Приложение Happ — это лишь инструмент для подключения. Оно не предоставляет доступ к VPN-серверам само по себе. Конфигурацию (ключ) для подключения вы получаете от вашего VPN-провайдера.</p></div><div class="tip custom-block"><p class="custom-block-title">Вам нужен ключ от всех дверей?</p><p>Все инструкции в этом гайде предполагают, что у вас уже есть <strong>ключ доступа TeslaVPN</strong>. Он — ваш универсальный пропуск в мир заблокированных сервисов и свободного интернета.</p><p>Если у вас его еще нет, получить его можно за минуту.</p><p><a href="https://t.me/vpntesla_bot?start=docs" target="_blank" rel="noreferrer"><strong>➡️ Получить ключ в нашем Telegram-боте</strong></a></p></div><h2 id="шаг-1-установка-приложения" tabindex="-1">Шаг 1: Установка приложения <a class="header-anchor" href="#шаг-1-установка-приложения" aria-label="Permalink to &quot;Шаг 1: Установка приложения&quot;">​</a></h2><ol><li>Перейдите по ссылке выше и установите <strong>Happ</strong> из Google Play.</li></ol><p><img src="'+s+'" alt="установка" data-zoomable="true"></p><div class="warning custom-block"><p class="custom-block-title">Google Play недоступен?</p><p>Если у вас нет доступа к магазину приложений Google, вы можете скачать установочный <strong><code>.apk</code></strong> файл по прямой ссылке выше и установить его вручную. Не забудьте разрешить установку из неизвестных источников в настройках вашего устройства.</p></div><h2 id="шаг-2-подключение" tabindex="-1">Шаг 2: Подключение <a class="header-anchor" href="#шаг-2-подключение" aria-label="Permalink to &quot;Шаг 2: Подключение&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">Где взять ключ?</p><p>Ваш персональный ключ для подключения доступен в нашем Telegram-боте: <a href="https://t.me/vpntesla_bot?start=docs" target="_blank" rel="noreferrer"><strong>@vpntesla_bot</strong></a>. Просто скопируйте его.</p></div><ol><li>Скопируйте ваш ключ (длинную ссылку) из бота. <img src="'+l+'" alt="установка" data-zoomable="true"></li><li>Откройте приложение Happ.</li><li>Выберите пункт <strong>«Из буфера обмена»</strong> (<code>From Clipboard</code>).</li><li>Дождитесь когда подписка добавиться, нажимаете подключиться наверху - <strong>Готово!</strong> Можно пользоваться! <img src="'+n+'" alt="установка" data-zoomable="true"></li></ol><h2 id="как-обновить-список-серверов" tabindex="-1">Как обновить список серверов? <a class="header-anchor" href="#как-обновить-список-серверов" aria-label="Permalink to &quot;Как обновить список серверов?&quot;">​</a></h2><p>Чтобы получить доступ к новым локациям или обновить параметры соединения, просто нажмите на <strong>иконку обновления</strong> (две стрелки по кругу) рядом с названием вашей подписки. <img src="'+i+'" alt="установка" data-zoomable="true"></p><hr><div class="info custom-block"><p class="custom-block-title">Возникли трудности?</p><p>Если у вас возникли проблемы с настройкой или подключением, наша служба поддержки всегда готова помочь. Напишите нам: <a href="https://t.me/Tesla_support_vpn" target="_blank" rel="noreferrer"><strong>@Tesla_support_vpn</strong></a>.</p></div><p style="text-align:right;color:var(--vp-c-text-2);font-size:0.875rem;">Последнее обновление: 19 июля 2025 г.</p>',19)]))}const P=o(d,[["render",c]]);export{f as __pageData,P as default};
