import{_ as a}from"./chunks/v2RayTun.oPaynP2S.js";import{_ as o,c as e,o as r,ag as n}from"./chunks/framework.Brz8Y0aD.js";const l="/img/apps/android/v2raytun/install_4.png",s="/img/apps/android/v2raytun/install_1.png",i="/img/apps/android/v2raytun/install_3.png",d="/img/apps/android/v2raytun/install_5.png",p="/img/apps/android/v2raytun/install_2.png",T=JSON.parse('{"title":"Настройка v2RayTun на Android","description":"Инструкция по установке и настройке приложения v2RayTun на Android для подключения к Tesla VPN, включая раздельное туннелирование.","frontmatter":{"id":"setup-v2raytun-android","title":"Настройка v2RayTun на Android","description":"Инструкция по установке и настройке приложения v2RayTun на Android для подключения к Tesla VPN, включая раздельное туннелирование.","keywords":["v2RayTun","Tesla VPN","VLESS","настройка VPN на Android","раздельное туннелирование"],"head":[["meta",{"name":"og:image"},{"content":"/img/banners/v2raytun.png"}]]},"headers":[],"relativePath":"ConnectDevice/Android/v2RayTun.md","filePath":"ConnectDevice/Android/v2RayTun.md","lastUpdated":null}'),c={name:"ConnectDevice/Android/v2RayTun.md"};function g(u,t,m,v,_,h){return r(),e("div",null,t[0]||(t[0]=[n('<p><img src="'+a+'" alt="Шапка" data-zoomable="true"></p><h1 id="настроика-v2raytun-на-android" tabindex="-1">Настройка v2RayTun на Android <a class="header-anchor" href="#настроика-v2raytun-на-android" aria-label="Permalink to &quot;Настройка v2RayTun на Android&quot;">​</a></h1><p>Эта инструкция поможет вам установить и настроить приложение <strong>v2RayTun</strong> для подключения к <strong>Tesla VPN</strong> на вашем Android-устройстве.</p><table tabindex="0"><thead><tr><th style="text-align:center;">Требования</th><th style="text-align:center;">Ссылка для загрузки</th></tr></thead><tbody><tr><td style="text-align:center;">Android 7.0 или новее</td><td style="text-align:center;"><a href="https://play.google.com/store/apps/details?id=com.v2raytun.android" target="_blank" rel="noreferrer"><strong>Скачать из Google Play</strong></a></td></tr></tbody></table><blockquote><p>v2RayTun — это современный мультиплатформенный клиент, который славится своей простотой, скоростью и удобной функцией раздельного туннелирования.</p></blockquote><div class="danger custom-block"><p class="custom-block-title">Важная информация</p><p>Приложение v2RayTun — это лишь инструмент для подключения. Оно не предоставляет доступ к VPN-серверам само по себе. Конфигурацию (ключ) для подключения вы получаете от вашего VPN-провайдера.</p></div><div class="tip custom-block"><p class="custom-block-title">Вам нужен ключ от всех дверей?</p><p>Все инструкции в этом гайде предполагают, что у вас уже есть <strong>ключ доступа TeslaVPN</strong>. Он — ваш универсальный пропуск в мир заблокированных сервисов и свободного интернета.</p><p>Если у вас его еще нет, получить его можно за минуту.</p><p><a href="https://t.me/vpntesla_bot?start=docs" target="_blank" rel="noreferrer"><strong>➡️ Получить ключ в нашем Telegram-боте</strong></a></p></div><h2 id="шаг-1-установка-приложения" tabindex="-1">Шаг 1: Установка приложения <a class="header-anchor" href="#шаг-1-установка-приложения" aria-label="Permalink to &quot;Шаг 1: Установка приложения&quot;">​</a></h2><ol><li>Перейдите по ссылке выше и установите <strong>v2RayTun</strong> из Google Play.</li></ol><p><img src="'+l+'" alt="установка" data-zoomable="true"></p><div class="warning custom-block"><p class="custom-block-title">Google Play недоступен?</p><p>Вы можете скачать <code>.apk</code> файл с <a href="https://v2raytun.com/" target="_blank" rel="noreferrer"><strong>официального сайта проекта</strong></a>.</p></div><h2 id="шаг-2-добавление-ключа-tesla-vpn" tabindex="-1">Шаг 2: Добавление ключа Tesla VPN <a class="header-anchor" href="#шаг-2-добавление-ключа-tesla-vpn" aria-label="Permalink to &quot;Шаг 2: Добавление ключа Tesla VPN&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">Где взять ключ?</p><p>Ваш персональный ключ для подключения доступен в нашем Telegram-боте: <a href="https://t.me/vpntesla_bot?start=docs" target="_blank" rel="noreferrer"><strong>@vpntesla_bot</strong></a>. Просто скопируйте его.</p></div><ol><li>Скопируйте ваш ключ (длинную ссылку) из бота.</li></ol><p><img src="'+s+'" alt="установка" data-zoomable="true"></p><h2 id="шаг-3-подключение" tabindex="-1">Шаг 3: Подключение <a class="header-anchor" href="#шаг-3-подключение" aria-label="Permalink to &quot;Шаг 3: Подключение&quot;">​</a></h2><ol><li>Откройте приложение v2RayTun.</li><li>Нажмите на плюс «+» в правом верхнем углу вкладки «Подключение»</li><li>Выберите «Импорт из буфера обмена»</li><li>Нажмите кнопку наверху экрана для подключения.</li></ol><p><img src="'+i+'" alt="установка" data-zoomable="true"></p><p><strong>Готово!</strong> Вы подключены к сети.</p><h2 id="как-обновить-список-серверов" tabindex="-1">Как обновить список серверов? <a class="header-anchor" href="#как-обновить-список-серверов" aria-label="Permalink to &quot;Как обновить список серверов?&quot;">​</a></h2><p>На главном экране нажмите на <strong>иконку обновления</strong> (две стрелки по кругу) рядом с надписью TeslaVPN.</p><p><img src="'+d+'" alt="установка" data-zoomable="true"></p><h2 id="исключение-доменов-и-сервисов" tabindex="-1">Исключение доменов и сервисов <a class="header-anchor" href="#исключение-доменов-и-сервисов" aria-label="Permalink to &quot;Исключение доменов и сервисов&quot;">​</a></h2><ol><li>Перейдите на вкладку <strong>«Сервисы»</strong>.</li><li>Активируйте переключатель <strong>«Включить сервис»</strong>.</li><li>Поставьте галочки напротив доменов и сервисов, которые должны работать <strong>в обход VPN</strong>.</li><li><strong>Рекомендуемые пункты:</strong><ul><li><strong>Домены:</strong> <code>.ru</code>, <code>.рф</code>, <code>.su</code></li><li><strong>Сервисы:</strong> Avito, Ozon, СберБанк, VK, Wildberries, Яндекс, T-Bank</li></ul></li></ol><p><img src="'+p+'" alt="установка" data-zoomable="true"></p><div class="tip custom-block"><p class="custom-block-title">Возникли трудности?</p><p>Если у вас возникли проблемы с настройкой или подключением, наша служба поддержки всегда готова помочь. Напишите нам: <a href="https://t.me/Tesla_vpn_support" target="_blank" rel="noreferrer"><strong>@Tesla_vpn_support</strong></a>.</p></div><p style="text-align:right;color:var(--vp-c-text-2);font-size:0.875rem;">Последнее обновление: 19 июля 2025 г.</p>',27)]))}const k=o(c,[["render",g]]);export{T as __pageData,k as default};
