import{_ as o}from"./chunks/v2Box.C4kEv4fe.js";import{_ as e,c as a,o as s,ag as r}from"./chunks/framework.Brz8Y0aD.js";const l="/img/apps/ios/v2box/install_1.png",i="/img/apps/ios/v2box/install_2.png",n="/img/apps/ios/v2box/install_3.png",p="/img/apps/ios/v2box/install_4.png",V=JSON.parse('{"title":"Настройка V2Box на iOS","description":"Пошаговая инструкция по установке и настройке приложения V2Box на iPhone и iPad для подключения к Tesla VPN по протоколу VLESS.","frontmatter":{"id":"setup-v2box-ios","title":"Настройка V2Box на iOS","description":"Пошаговая инструкция по установке и настройке приложения V2Box на iPhone и iPad для подключения к Tesla VPN по протоколу VLESS.","keywords":["V2Box","Tesla VPN","VLESS","настройка VPN на iOS","VPN для iPhone","как настроить V2Box"],"head":[["meta",{"name":"og:image"},{"content":"/img/banners/v2box.png"}]]},"headers":[],"relativePath":"ConnectDevice/IOS/V2Box.md","filePath":"ConnectDevice/IOS/V2Box.md","lastUpdated":null}'),c={name:"ConnectDevice/IOS/V2Box.md"};function g(d,t,m,h,b,_){return s(),a("div",null,t[0]||(t[0]=[r('<p><img src="'+o+'" alt="Шапка" data-zoomable="true"></p><h1 id="настроика-v2box-на-iphone-и-ipad" tabindex="-1">Настройка V2Box на iPhone и iPad <a class="header-anchor" href="#настроика-v2box-на-iphone-и-ipad" aria-label="Permalink to &quot;Настройка V2Box на iPhone и iPad&quot;">​</a></h1><p>Эта инструкция поможет вам установить и настроить приложение <strong>V2Box</strong> для подключения к <strong>Tesla VPN</strong> на вашем устройстве с iOS или iPadOS.</p><table tabindex="0"><thead><tr><th style="text-align:center;">Требования</th><th style="text-align:center;">Ссылка для загрузки</th></tr></thead><tbody><tr><td style="text-align:center;">iOS 14.0 или новее</td><td style="text-align:center;"><a href="https://apps.apple.com/us/app/v2box-v2ray-client/id6446814690" target="_blank" rel="noreferrer"><strong>Скачать из App Store</strong></a></td></tr></tbody></table><blockquote><p>V2Box — это простой и функциональный клиент для iOS с поддержкой протокола VLESS, который используется в <strong>Tesla VPN</strong>.</p></blockquote><div class="danger custom-block"><p class="custom-block-title">Важная информация</p><p>Приложение V2Box — это лишь инструмент для подключения. Оно не предоставляет доступ к VPN-серверам само по себе. Конфигурацию (ключ) для подключения вы получаете от вашего VPN-провайдера.</p></div><div class="tip custom-block"><p class="custom-block-title">Вам нужен ключ от всех дверей?</p><p>Все инструкции в этом гайде предполагают, что у вас уже есть <strong>ключ доступа TeslaVPN</strong>. Он — ваш универсальный пропуск в мир заблокированных сервисов и свободного интернета.</p><p>Если у вас его еще нет, получить его можно за минуту.</p><p><a href="https://t.me/vpntesla_bot?start=docs" target="_blank" rel="noreferrer"><strong>➡️ Получить ключ в нашем Telegram-боте</strong></a></p></div><h2 id="шаг-1-установка-приложения" tabindex="-1">Шаг 1: Установка приложения <a class="header-anchor" href="#шаг-1-установка-приложения" aria-label="Permalink to &quot;Шаг 1: Установка приложения&quot;">​</a></h2><ol><li>Перейдите по ссылке выше или найдите <strong>V2Box</strong> в App Store.</li><li>Нажмите кнопку &quot;Загрузить&quot; и установите приложение.</li></ol><p><img src="'+l+'" alt="установка" data-zoomable="true"></p><div class="warning custom-block"><p class="custom-block-title">Не можете найти приложение?</p><p>Иногда приложения для VPN могут быть недоступны в российском App Store. В этом случае воспользуйтесь нашей <a href="/guides/change-appstore-region.html"><strong>инструкцией по смене региона Apple ID</strong></a>.</p></div><h2 id="шаг-2-добавление-ключа-tesla-vpn" tabindex="-1">Шаг 2: Добавление ключа Tesla VPN <a class="header-anchor" href="#шаг-2-добавление-ключа-tesla-vpn" aria-label="Permalink to &quot;Шаг 2: Добавление ключа Tesla VPN&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">Где взять ключ?</p><p>Ваш персональный ключ для подключения доступен в нашем Telegram-боте: <a href="https://t.me/vpntesla_bot?start=docs" target="_blank" rel="noreferrer"><strong>@vpntesla_bot</strong></a>. Просто скопируйте его.</p></div><ol><li>Скопируйте ваш ключ (длинную ссылку) из бота.</li></ol><p><img src="'+i+'" alt="установка" data-zoomable="true"></p><p>Подписка <strong>Tesla VPN</strong> будет добавлена в список.</p><h2 id="шаг-3-подключение" tabindex="-1">Шаг 3: Подключение <a class="header-anchor" href="#шаг-3-подключение" aria-label="Permalink to &quot;Шаг 3: Подключение&quot;">​</a></h2><ol><li>Перейдите на вкладку <strong>«Configs»</strong>.</li><li>Нажмите на <strong>плюсик</strong> наверху.</li><li>Выберите <strong>«Import v2ray url from clipboard»</strong>.</li><li>Проведите по слайдеру внизу экрана для подключения.</li><li>Разрешите добавление VPN-конфигурации в настройках iOS, если приложение попросит об этом.</li></ol><p><img src="'+n+'" alt="установка" data-zoomable="true"></p><p><strong>Готово!</strong> Вы подключены к безопасному интернету.</p><h2 id="как-обновить-список-серверов" tabindex="-1">Как обновить список серверов? <a class="header-anchor" href="#как-обновить-список-серверов" aria-label="Permalink to &quot;Как обновить список серверов?&quot;">​</a></h2><p>Чтобы получить доступ к новым локациям или обновить параметры соединения:</p><ol><li>Перейдите на вкладку <strong>«Configs»</strong>.</li><li>Нажмите на значок стрелочек по кругу. <img src="'+p+'" alt="установка" data-zoomable="true"></li></ol><div class="info custom-block"><p class="custom-block-title">Возникли трудности?</p><p>Если у вас возникли проблемы с настройкой или подключением, наша служба поддержки всегда готова помочь. Напишите нам: <a href="https://t.me/Tesla_support_vpn" target="_blank" rel="noreferrer"><strong>@Tesla_support_vpn</strong></a>.</p></div><p style="text-align:right;color:var(--vp-c-text-2);font-size:0.875rem;">Последнее обновление: 19 июля 2025 г.</p>',25)]))}const v=e(c,[["render",g]]);export{V as __pageData,v as default};
