import{_ as e}from"./chunks/hiddify.BPzpiyj4.js";import{_ as l,c as i,o,ag as s}from"./chunks/framework.Brz8Y0aD.js";const a="/img/apps/windows/hiddify/install_2.png",d="/img/apps/windows/hiddify/install_1.png",r="/img/apps/windows/hiddify/install_5.png",n="/img/apps/windows/hiddify/install_3.png",p="/img/apps/windows/hiddify/install_4.png",x=JSON.parse('{"title":"Настройка Hiddify на Windows","description":"Пошаговая инструкция по установке и продвинутой настройке приложения Hiddify на Windows для подключения к Tesla VPN, включая раздельное туннелирование.","frontmatter":{"id":"setup-hiddify-windows","title":"Настройка Hiddify на Windows","description":"Пошаговая инструкция по установке и продвинутой настройке приложения Hiddify на Windows для подключения к Tesla VPN, включая раздельное туннелирование.","keywords":["Hiddify","Tesla VPN","VLESS","настройка VPN на Windows","VPN для ПК","раздельное туннелирование"],"head":[["meta",{"name":"og:image"},{"content":"/img/banners/hiddify.png"}]]},"headers":[],"relativePath":"ConnectDevice/Windows/Hiddify.md","filePath":"ConnectDevice/Windows/Hiddify.md","lastUpdated":null}'),c={name:"ConnectDevice/Windows/Hiddify.md"};function g(f,t,h,m,y,_){return o(),i("div",null,t[0]||(t[0]=[s('<p><img src="'+e+'" alt="Шапка" data-zoomable="true"></p><h1 id="настроика-hiddify-на-windows" tabindex="-1">Настройка Hiddify на Windows <a class="header-anchor" href="#настроика-hiddify-на-windows" aria-label="Permalink to &quot;Настройка Hiddify на Windows&quot;">​</a></h1><p>Эта инструкция поможет вам установить и настроить приложение <strong>Hiddify</strong> для подключения к <strong>Tesla VPN</strong> на вашем ПК с Windows.</p><table tabindex="0"><thead><tr><th style="text-align:center;">Требования</th><th style="text-align:center;">Ссылки для загрузки</th></tr></thead><tbody><tr><td style="text-align:center;">Windows 10 или новее</td><td style="text-align:center;"><a href="https://github.com/hiddify/hiddify-app/releases" target="_blank" rel="noreferrer"><strong>Скачать с GitHub (.exe)</strong></a> | <a href="https://apps.microsoft.com/detail/9NL87T1R663L" target="_blank" rel="noreferrer"><strong>Microsoft Store</strong></a></td></tr></tbody></table><blockquote><p>Hiddify — это мощный мультиплатформенный клиент, доступный на всех популярных ОС и поддерживающий гибкие настройки маршрутизации трафика.</p></blockquote><div class="danger custom-block"><p class="custom-block-title">Важная информация</p><p>Приложение Hiddify — это лишь инструмент для подключения. Оно не предоставляет доступ к VPN-серверам само по себе. Конфигурацию (ключ) для подключения вы получаете от вашего VPN-провайдера.</p></div><div class="tip custom-block"><p class="custom-block-title">Вам нужен ключ от всех дверей?</p><p>Все инструкции в этом гайде предполагают, что у вас уже есть <strong>ключ доступа TeslaVPN</strong>. Он — ваш универсальный пропуск в мир заблокированных сервисов и свободного интернета.</p><p>Если у вас его еще нет, получить его можно за минуту.</p><p><a href="https://t.me/vpntesla_bot?start=docs" target="_blank" rel="noreferrer"><strong>➡️ Получить ключ в нашем Telegram-боте</strong></a></p></div><h2 id="шаг-1-установка-приложения" tabindex="-1">Шаг 1: Установка приложения <a class="header-anchor" href="#шаг-1-установка-приложения" aria-label="Permalink to &quot;Шаг 1: Установка приложения&quot;">​</a></h2><ol><li>Загрузите установочный файл Hiddify по одной из ссылок выше. <img src="'+a+'" alt="установка" data-zoomable="true"></li><li>Запустите установщик. Если появится предупреждение системы безопасности, разрешите установку. <img src="'+d+'" alt="установка" data-zoomable="true"></li><li>Следуйте шагам установщика.</li></ol><div class="warning custom-block"><p class="custom-block-title">Запуск от имени администратора</p><p>Для корректной работы VPN-режима рекомендуется всегда запускать Hiddify с правами администратора.</p><ol><li>Найдите ярлык Hiddify на рабочем столе.</li><li>Нажмите на него правой кнопкой мыши -&gt; <strong>«Свойства»</strong>.</li><li>Перейдите на вкладку <strong>«Совместимость»</strong>.</li><li>Поставьте галочку <strong>«Запускать эту программу от имени администратора»</strong> и нажмите <strong>OK</strong>.</li></ol></div><h2 id="шаг-2-добавление-ключа-tesla-vpn" tabindex="-1">Шаг 2: Добавление ключа Tesla VPN <a class="header-anchor" href="#шаг-2-добавление-ключа-tesla-vpn" aria-label="Permalink to &quot;Шаг 2: Добавление ключа Tesla VPN&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">Где взять ключ?</p><p>Ваш персональный ключ для подключения доступен в нашем Telegram-боте: <a href="https://t.me/vpntesla_bot?start=docs" target="_blank" rel="noreferrer"><strong>@vpntesla_bot</strong></a>. Просто скопируйте его.</p></div><ol><li>Скопируйте ваш ключ (длинную ссылку) из бота.</li></ol><p><img src="'+r+'" alt="установка" data-zoomable="true"></p><h2 id="настроика-перед-подключением" tabindex="-1">Настройка перед подключением: <a class="header-anchor" href="#настроика-перед-подключением" aria-label="Permalink to &quot;Настройка перед подключением:&quot;">​</a></h2><ol><li>В приложении перейдите на вкладку <strong>«Параметры конфигурации»</strong>.</li></ol><p><img src="'+n+'" alt="установка" data-zoomable="true"></p><ol start="2"><li>Установите (проверьте) следующие значения:</li></ol><table tabindex="0"><thead><tr><th style="text-align:left;">Параметр</th><th style="text-align:left;">Значение</th></tr></thead><tbody><tr><td style="text-align:left;">Регион</td><td style="text-align:left;">Другой</td></tr><tr><td style="text-align:left;">Блокировать рекламу</td><td style="text-align:left;">По желанию</td></tr><tr><td style="text-align:left;">Маршрутизация IPv6</td><td style="text-align:left;">Отключено</td></tr><tr><td style="text-align:left;">Удаленный DNS</td><td style="text-align:left;"><code>udp://8.8.8.8</code></td></tr><tr><td style="text-align:left;">Стратегия удалённого домена DNS</td><td style="text-align:left;"><code>ipv4_only</code></td></tr><tr><td style="text-align:left;">Прямой DNS</td><td style="text-align:left;"><code>tcp://8.8.8.8</code> или <code>https://dns.google/dns-query</code></td></tr><tr><td style="text-align:left;">Стратегия прямого домена DNS</td><td style="text-align:left;"><code>ipv4_only</code></td></tr><tr><td style="text-align:left;">Режим работы</td><td style="text-align:left;">VPN</td></tr></tbody></table><h2 id="шаг-3-подключение" tabindex="-1">Шаг 3: Подключение <a class="header-anchor" href="#шаг-3-подключение" aria-label="Permalink to &quot;Шаг 3: Подключение&quot;">​</a></h2><ol><li><p>Откройте приложение Hiddify.</p></li><li><p>Нажмите на большую кнопку <strong>«+ Add Profile»</strong>.</p></li><li><p>Выберите <strong>«Добавить из буфера обмена»</strong> (<code>Import from Clipboard</code>). Дождитесь сообщения об успешном импорте вашего профиля <strong>Tesla VPN</strong>.</p></li><li><p>Убедитесь, что ваш профиль <strong>Tesla VPN</strong> выбран на главном экране.</p></li><li><p>Нажмите большую круглую кнопку для подключения.</p></li></ol><p><img src="'+p+'" alt="установка" data-zoomable="true"></p><p><strong>Готово!</strong> Вы в сети. Чтобы выбрать конкретную страну, перейдите на вкладку <strong>«Прокси»</strong>.</p><div class="info custom-block"><p class="custom-block-title">Возникли трудности?</p><p>Если у вас возникли проблемы с настройкой или подключением, наша служба поддержки всегда готова помочь. Напишите нам: <a href="https://t.me/Tesla_vpn_support" target="_blank" rel="noreferrer"><strong>@Tesla_vpn_support</strong></a>.</p></div><p style="text-align:right;color:var(--vp-c-text-2);font-size:0.875rem;">Последнее обновление: 19 июля 2025 г.</p>',25)]))}const w=l(c,[["render",g]]);export{x as __pageData,w as default};
