import{_ as a}from"./chunks/v2RayTun.oPaynP2S.js";import{_ as o,c as s,o as n,ag as e}from"./chunks/framework.Brz8Y0aD.js";const l="/img/apps/windows/v2raytun/install_1.png",r="/img/apps/windows/v2raytun/install_6.png",i="/img/apps/windows/v2raytun/install_4.png",p="/img/apps/windows/v2raytun/install_7.png",c="/img/apps/windows/v2raytun/install_5.png",T=JSON.parse('{"title":"Настройка v2RayTun на Windows","description":"Инструкция по установке и настройке приложения v2RayTun на Windows для подключения к Tesla VPN, включая раздельное туннелирование.","frontmatter":{"id":"setup-v2raytun-windows","title":"Настройка v2RayTun на Windows","description":"Инструкция по установке и настройке приложения v2RayTun на Windows для подключения к Tesla VPN, включая раздельное туннелирование.","keywords":["v2RayTun","Tesla VPN","VLESS","настройка VPN на Windows","VPN для ПК","раздельное туннелирование"],"head":[["meta",{"name":"og:image"},{"content":"/img/banners/v2raytun.png"}]]},"headers":[],"relativePath":"ConnectDevice/Windows/v2RayTun.md","filePath":"ConnectDevice/Windows/v2RayTun.md","lastUpdated":null}'),d={name:"ConnectDevice/Windows/v2RayTun.md"};function g(u,t,m,h,v,_){return n(),s("div",null,t[0]||(t[0]=[e('<p><img src="'+a+'" alt="Шапка" data-zoomable="true"></p><h1 id="настроика-v2raytun-на-windows" tabindex="-1">Настройка v2RayTun на Windows <a class="header-anchor" href="#настроика-v2raytun-на-windows" aria-label="Permalink to &quot;Настройка v2RayTun на Windows&quot;">​</a></h1><p>Эта инструкция поможет вам установить и настроить приложение <strong>v2RayTun</strong> для подключения к <strong>Tesla VPN</strong> на вашем ПК с Windows.</p><table tabindex="0"><thead><tr><th style="text-align:center;">Требования</th><th style="text-align:center;">Ссылка для загрузки</th></tr></thead><tbody><tr><td style="text-align:center;">Windows 8.1 или новее</td><td style="text-align:center;"><a href="https://v2raytun.com/" target="_blank" rel="noreferrer"><strong>Скачать с сайта (.exe)</strong></a></td></tr></tbody></table><blockquote><p>v2RayTun — это современный мультиплатформенный клиент, который славится своей простотой, скоростью и удобной функцией раздельного туннелирования.</p></blockquote><div class="danger custom-block"><p class="custom-block-title">Важная информация</p><p>Приложение v2RayTun — это лишь инструмент для подключения. Оно не предоставляет доступ к VPN-серверам само по себе. Конфигурацию (ключ) для подключения вы получаете от вашего VPN-провайдера.</p></div><div class="tip custom-block"><p class="custom-block-title">Вам нужен ключ от всех дверей?</p><p>Все инструкции в этом гайде предполагают, что у вас уже есть <strong>ключ доступа TeslaVPN</strong>. Он — ваш универсальный пропуск в мир заблокированных сервисов и свободного интернета.</p><p>Если у вас его еще нет, получить его можно за минуту.</p><p><a href="https://t.me/vpntesla_bot?start=docs" target="_blank" rel="noreferrer"><strong>➡️ Получить ключ в нашем Telegram-боте</strong></a></p></div><h2 id="шаг-1-установка-приложения" tabindex="-1">Шаг 1: Установка приложения <a class="header-anchor" href="#шаг-1-установка-приложения" aria-label="Permalink to &quot;Шаг 1: Установка приложения&quot;">​</a></h2><ol><li>Загрузите установочный файл v2RayTun по ссылке выше.</li><li>Запустите установщик. Если появится предупреждение системы безопасности, разрешите установку.</li><li>Следуйте шагам установщика.</li></ol><p><img src="'+l+'" alt="установка" data-zoomable="true"></p><div class="warning custom-block"><p class="custom-block-title">Запуск от имени администратора</p><p>Для корректной работы VPN-режима рекомендуется всегда запускать v2RayTun с правами администратора. Настройте это один раз:</p><ol><li>Найдите ярлык v2RayTun на рабочем столе.</li><li>Нажмите на него правой кнопкой мыши -&gt; <strong>«Свойства»</strong>.</li><li>Перейдите на вкладку <strong>«Совместимость»</strong> -&gt; Поставьте галочку <strong>«Запускать эту программу от имени администратора»</strong> -&gt; <strong>OK</strong>.</li></ol></div><h2 id="шаг-2-добавление-ключа-tesla-vpn" tabindex="-1">Шаг 2: Добавление ключа Tesla VPN <a class="header-anchor" href="#шаг-2-добавление-ключа-tesla-vpn" aria-label="Permalink to &quot;Шаг 2: Добавление ключа Tesla VPN&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">Где взять ключ?</p><p>Ваш персональный ключ для подключения доступен в нашем Telegram-боте: <a href="https://t.me/vpntesla_bot?start=docs" target="_blank" rel="noreferrer"><strong>@vpntesla_bot</strong></a>. Просто скопируйте его.</p></div><ol><li>Скопируйте ваш ключ (длинную ссылку) из бота.</li></ol><p><img src="'+r+'" alt="установка" data-zoomable="true"></p><h2 id="шаг-3-подключение" tabindex="-1">Шаг 3: Подключение <a class="header-anchor" href="#шаг-3-подключение" aria-label="Permalink to &quot;Шаг 3: Подключение&quot;">​</a></h2><ol><li>Откройте приложение v2RayTun.</li><li>Нажмите на плюс «+» в правом верхнем углу вкладки «Подключение»</li><li>Выберите «Импорт из буфера обмена»</li><li>Нажмите кнопку наверху экрана для подключения.</li></ol><p><img src="'+i+'" alt="установка" data-zoomable="true"></p><p><strong>Готово!</strong> Вы подключены к сети.</p><h2 id="как-обновить-список-серверов" tabindex="-1">Как обновить список серверов? <a class="header-anchor" href="#как-обновить-список-серверов" aria-label="Permalink to &quot;Как обновить список серверов?&quot;">​</a></h2><p>На главном экране нажмите на <strong>иконку обновления</strong> (две стрелки по кругу) в правом нижнем углу.</p><p><img src="'+p+'" alt="установка" data-zoomable="true"></p><h2 id="продвинутая-настроика-раздельное-туннелирование" tabindex="-1">Продвинутая настройка: Раздельное туннелирование <a class="header-anchor" href="#продвинутая-настроика-раздельное-туннелирование" aria-label="Permalink to &quot;Продвинутая настройка: Раздельное туннелирование&quot;">​</a></h2><p>v2RayTun позволяет очень легко настроить <strong>раздельное туннелирование</strong> (Split Tunneling).</p><div class="info custom-block"><p class="custom-block-title">Что это даёт?</p><ul><li><strong>Не нужно постоянно включать/выключать VPN</strong>: банковские приложения, госуслуги и онлайн-игры будут работать как обычно.</li><li><strong>Экономия трафика</strong>: трафик к локальным ресурсам не будет расходовать ваш лимит.</li><li><strong>Высокая скорость</strong>: доступ к локальным сайтам будет максимально быстрым.</li></ul></div><h3 id="исключение-доменов-и-сервисов" tabindex="-1">Исключение доменов и сервисов <a class="header-anchor" href="#исключение-доменов-и-сервисов" aria-label="Permalink to &quot;Исключение доменов и сервисов&quot;">​</a></h3><ol><li>В приложении перейдите на вкладку <strong>«Сервисы»</strong>.</li><li>Активируйте переключатель <strong>«Включить сервис»</strong>.</li><li>Поставьте галочки напротив доменов и сервисов, которые должны работать <strong>в обход VPN</strong>.</li></ol><h3 id="исключение-приложении-для-игр" tabindex="-1">Исключение приложений (для игр) <a class="header-anchor" href="#исключение-приложении-для-игр" aria-label="Permalink to &quot;Исключение приложений (для игр)&quot;">​</a></h3><ol><li>Перейдите на вкладку <strong>«Маршрутизация»</strong>.</li><li>Перейдите в <strong>«Маршрутизация приложений»</strong>.</li><li>Выберите режим <strong>«Приложения в обход VPN»</strong> и нажмите <strong>«Добавить»</strong>, чтобы выбрать <code>.exe</code> файл вашей игры или программы.</li><li>Переподключитесь, чтобы изменения вступили в силу.</li></ol><p><img src="'+c+'" alt="установка" data-zoomable="true"></p><hr><div class="tip custom-block"><p class="custom-block-title">Возникли трудности?</p><p>Если у вас возникли проблемы с настройкой или подключением, наша служба поддержки всегда готова помочь. Напишите нам: <a href="https://t.me/Tesla_support_vpn" target="_blank" rel="noreferrer"><strong>@Tesla_support_vpn</strong></a>.</p></div><p style="text-align:right;color:var(--vp-c-text-2);font-size:0.875rem;">Последнее обновление: 19 июля 2025 г.</p>',33)]))}const w=o(d,[["render",g]]);export{T as __pageData,w as default};
